import actions from './actions'


const initialState = {
    quizes: [],
    loading: false,
    error: null
};

export default function quizReducer(state = initialState, action) {
    switch (action.type) {
        case FETCH_LOADING:
            return {
                ...state,
                loading: true
            };
        case FETCH_SUCCESS:
            return {
                ...state,
                loading: false,
                quizes: action.quizes
            };
        case FETCH_ERROR:
            return {
                ...state,
                loading: false,
                error: action.error
            };
        default:
            return state
    }
}
