import actions from './actions'

const initialState = {
    items: [],
    loading: false,
    error: null
};

export default function reducer (state = initialState, action) {
    switch (action.type) {
        case 'LOADING':
            return {
                ...state,
                loading: true,
                error: null
            };

        case 'SUCCESS':
            return {
                ...state,
                loading: false,
                items: action.payload.products
            };
        case 'ERROR' :
            return {
               ...state,
               loading: true,
               error: action
            };

        default:
            return state

    }
}
