export const loadingData = () => ({
    type: 'LOADING'
});

export const successData = products => ({
    type: 'SUCCESS',
    payload: { products }
});

export const errorData = error => ({
    type: 'ERROR',
    payload: { error }
});


function getProducts() {
    return fetch("/products")
        .then(handleErrors)
        .then(res => res.json());
}


function fakeGetProducts() {
    return new Promise(resolve => {
        // Resolve after a timeout so we can see the loading indicator
        setTimeout(
            () =>
                resolve({
                    products: [
                        {
                            id: 0,
                            name: "Apple"
                        },
                        {
                            id: 1,
                            name: "Bananas"
                        },
                        {
                            id: 2,
                            name: "Strawberries"
                        }
                    ]
                }),
            1000
        );
    });
}

export function fetchProducts() {
    return dispatch => {
        dispatch(loadingData());
        return fakeGetProducts()
            .then(json => {
                dispatch(successData(json.products));
                return json.products;
            })
            .catch(error =>
                dispatch(errorData(error))
            );
    };
}
